#!/bin/bash
for ini in *Client.ini; do
  TINI=&quot;$ini&quot;
done
TVER=&quot;`grep -i &quot;Version&quot; &quot;$TINI&quot; | awk -F &#039;=&#039; &#039;{ print $2 }&#039;`&quot;
if [ &quot;X$TVER&quot; = &quot;X&quot; ]; then
  echo &quot;Version not specified in $TINI&quot;
else
  echo &quot;$TVER&quot;
fi

