echo &quot;N/A on Mac&quot;

#!/bin/sh # Detects which OS and if it is Linux then it will detect which Linux # Distribution.
OS=`uname -s`
GetVersionFromFile() { VERSION=`cat $1 | tr &quot;\n&quot; &#039; &#039; | sed s/.*VERSION.*=\ // ` }
if [ &quot;${OS}&quot; = &quot;Darwin&quot; ] ; then # Opps, ran on Mac instead of Linux OS=Mac OSSTR=&quot;${OS} incorrect script&quot; elif [ &quot;${OS}&quot; = &quot;Linux&quot; ] ; then KERNEL=`uname -r` if [ -f /etc/redhat-release ] ; then DIST=&#039;RedHat&#039; PSUEDONAME=`cat /etc/redhat-release` REV=`cat /etc/redhat-release | sed s/.*release\ // | sed s/\ .*//` # just cat of redhat-release, to keep compatibility with old version # of sensor, but could do something different here OSSTR=&quot;${PSUEDONAME}&quot; elif [ -f /etc/SuSE-release ] ; then DIST=`cat /etc/SuSE-release | tr &quot;\n&quot; &#039; &#039;| sed s/VERSION.*//` REV=`cat /etc/SuSE-release | tr &quot;\n&quot; &#039; &#039; | sed s/.*=\ //` OSSTR=&quot;${DIST}&quot; elif [ -f /etc/lsb-release ] ; then # Ubuntu will use the lsb-release, but Debian should be caught by the next # if -- note that Ubuntu also has a /etc/debian_version # some other less popular distros may be caught by this too DIST=`awk -F= &#039;/^DISTRIB_DESCRIPTION/ {print $2}&#039; /etc/lsb-release| tr -d &#039;&quot;&#039;` OSSTR=&quot;${DIST}&quot; elif [ -f /etc/debian_version ] ; then DIST=&quot;Debian `cat /etc/debian_version`&quot; OSSTR=&quot;${DIST}&quot; else # Fall through, some Linux distributions we don&#039;t know or that does not # use lsb style names REV=`uname -r` OSSTR=&quot;Linux $REV&quot; fi else # in case this is run on something else -- like Solaris or BSD ir AIX or HP-UX OSSTR=&quot;${OS}&quot; fi
echo ${OSSTR}

#!/bin/bash
name=$(sw_vers | awk -F&#039;:\t&#039; &#039; /ProductName/ { print $2 }&#039;)
version=$(sw_vers | awk -F&#039;:\t&#039; &#039; /ProductVersion/ { print $2 }&#039;)
echo $name \($version\)

