echo &quot;N/A on Mac&quot;

#!/bin/sh # Detects which OS and if it is Linux then it will detect which Linux # Distribution.
# Can be used as a starting point for shell scripts that need to work differently on # Mac, and various flavors of Linux
OS=`uname -s`
GetVersionFromFile() { VERSION=`cat $1 | tr &quot;\n&quot; &#039; &#039; | sed s/.*VERSION.*=\ // ` }
if [ &quot;${OS}&quot; = &quot;Linux&quot; ] ; then KERNEL=`uname -r` if [ -f /etc/redhat-release ] ; then # Redhat based system rpm -qa --queryformat &quot;%{NAME}|%{VERSION}|nothing|Not Uninstallable\n&quot; elif [ -f /etc/SuSE-release ] ; then # SuSE / OpenSuSE based system rpm -qa --queryformat &quot;%{NAME}|%{VERSION}|nothing|Not Uninstallable\n&quot; elif [ -f /etc/lsb-release ] ; then # Usually Ubuntu -- this is a Linux Standard Based system, which # Ubuntu is by far the biggest. But general LSB commands should work dpkg -l | awk &#039;{print $2&quot;|&quot;$3&quot;|nothing|Not Uninstallable&quot;}&#039; elif [ -f /etc/debian_version ] ; then # Debian -- note that Ubuntu is Debian based, but Debian does not have a # lsb-release file dpkg -l | awk &#039;{print $2&quot;|&quot;$3&quot;|nothing|Not Uninstallable&quot;}&#039; #else # a less common distribution. Most all really popular distributions will # be caught by the tree above. fi #else # not Linux -- could be anything, BSD / Solaris / AIX / etc fi
echo ${OSSTR}

#!/bin/bash

notcontains() {
    string=&quot;$1&quot;
    substring=&quot;$2&quot;
    if test &quot;${string#*$substring}&quot; != &quot;$string&quot;
    then
        return 1    # $substring is not in $string
    else
        return 0    # $substring is in $string
    fi
}

ftemp=$(mktemp -t &#039;apps.xml&#039;)
system_profiler -xml SPApplicationsDataType &gt; &quot;$ftemp&quot;

count=1

while true
do
        app=`/usr/libexec/PlistBuddy -c &quot;Print :0:_items:$count:_name&quot; $ftemp 2&gt;/dev/null`
        version=`/usr/libexec/PlistBuddy -c &quot;Print :0:_items:$count:version&quot; $ftemp 2&gt;/dev/null`
        if [ -z &quot;$app&quot; ]; then
                break
        fi
        test=&quot;VMware&quot;
        if notcontains &quot;$version&quot; &quot;$test&quot;; then
                echo $app&quot;|&quot;$version&quot;|nothing|Not Uninstallable&quot;
        fi
        let count=count+1
done
rm -rf &quot;$ftemp&quot;

