echo &quot;N/A on Mac&quot;

#!/bin/sh # Detects which OS and if it is Linux then it will detect which Linux # Distribution.
# Can be used as a starting point for shell scripts that need to work differently on # Mac, and various flavors of Linux
OS=`uname -s`
GetVersionFromFile() { VERSION=`cat $1 | tr &quot;\n&quot; &#039; &#039; | sed s/.*VERSION.*=\ // ` }
if [ &quot;${OS}&quot; = &quot;Linux&quot; ] ; then KERNEL=`uname -r` if [ -f /etc/redhat-release ] ; then # Redhat based system chkconfig --list | grep 3:on | awk &#039;{print $1}&#039; elif [ -f /etc/SuSE-release ] ; then # SuSE / OpenSuSE based system ps aux | awk &#039;{print $11}&#039; elif [ -f /etc/lsb-release ] ; then # Usually Ubuntu -- this is a Linux Standard Based system, which # Ubuntu is by far the biggest. But general LSB commands should work ps aux | awk &#039;{print $11}&#039; elif [ -f /etc/debian_version ] ; then # Debian -- note that Ubuntu is Debian based, but Debian does not have a # lsb-release file ps aux | awk &#039;{print $11}&#039; #else # a less common distribution. Most all really popular distributions will # be caught by the tree above. fi #else # not Linux -- could be anything, BSD / Solaris / AIX / etc fi
echo ${OSSTR}

