#!/bin/sh

# THIS IS A STUB - NOT INTENDED AS FUNCTIONAL - NA
# 
# 

echo &quot;N/A on Mac&quot;

&#039;======================================== &#039; VirusScan Enterprise Version &#039;======================================== Const HKLM = &amp;H80000002
strComputer = &quot;.&quot; Set objRegistry = _ GetObject(&quot;winmgmts:{impersonationLevel=impersonate}!\\&quot; _ &amp; strComputer &amp; &quot;\root\default:StdRegProv&quot;)
&#039;Check for VirusScan key keyPath = &quot;Software\Network Associates\TVD\VirusScan&quot; key64Path = &quot;Software\WOW6432Node\Network Associates\TVD\VirusScan&quot;
If Not RegKeyExists(HKLM, keyPath) Then keyPath = key64Path End If
If RegKeyExists(HKLM, keyPath) Then objRegistry.GetStringValue HKLM,keyPath ,&quot;szCurrentVersionNumber&quot;, strVersion If Not IsNull(strVersion) Then WScript.Echo strVersion WScript.Quit End If End If
&#039;Check for VirusScan Enterprise key keyPath = &quot;Software\Network Associates\TVD\VirusScan Enterprise\CurrentVersion&quot; key64Path = &quot;Software\WOW6432Node\Network Associates\TVD\VirusScan Enterprise\CurrentVersion&quot;
If Not RegKeyExists(HKLM, keyPath) Then keyPath = key64Path End If
If RegKeyExists(HKLM, keyPath) Then objRegistry.GetStringValue HKLM,keyPath ,&quot;szProductVer&quot;, strVersion If Not IsNull(strVersion) Then WScript.Echo strVersion WScript.Quit End If End If
&#039;Check for DesktopProtection key keyPath = &quot;Software\McAfee\DesktopProtection&quot; key64Path = &quot;Software\WOW6432Node\McAfee\DesktopProtection&quot;
If Not RegKeyExists(HKLM, keyPath) Then keyPath = key64Path End If
If RegKeyExists(HKLM, keyPath) Then objRegistry.GetStringValue HKLM,keyPath ,&quot;szProductVer&quot;, strVersion If Not IsNull(strVersion) Then WScript.Echo strVersion WScript.Quit End If End If
WScript.Echo &quot;Not Installed&quot;
Function RegKeyExists(sHive, sRegKey) Dim aValueNames, aValueTypes If objRegistry.EnumValues(sHive, sRegKey, aValueNames, aValueTypes) = 0 Then RegKeyExists = True Else RegKeyExists = False End If End Function

