#!/bin/sh

# THIS IS A STUB - NOT INTENDED AS FUNCTIONAL - NA
# 
# 

echo &quot;N/A on Mac&quot;

&#039;======================================== &#039; Forefront Client Scheduled Scan Day &#039;========================================
Const HKLM = &amp;h80000002 Set reg=Getx64RegistryProvider()
&#039;Uses date math - set locale
SetLocale(1033)
strKey = &quot;Software\Microsoft\Microsoft Antimalware\Scan&quot; If Not RegKeyExists(reg, HKLM, strKey) Then strKey = &quot;Software\Wow6432Node\Microsoft\Microsoft Antimalware\Scan&quot; End If
&#039; check for windows defender (win 8 and &gt;) If Not RegKeyExists(reg, HKLM, strKey) Then strKey = &quot;Software\Microsoft\Windows Defender\Scan&quot; If Not RegKeyExists(reg, HKLM, strKey) Then strKey = &quot;Software\Wow6432Node\Microsoft\Windows Defender\Scan&quot; End If dIsDefender = 1 End If
If RegKeyExists(reg, HKLM, strKey) Then reg.GetDWORDValue HKLM, strKey, &quot;ScheduleDay&quot;, dValue
If Not IsNull(dValue) Then Select Case dValue Case 0 WScript.Echo &quot;Daily&quot;
Case 1 WScript.Echo &quot;Sunday&quot;
Case 2 WScript.Echo &quot;Monday&quot;
Case 3 WScript.Echo &quot;Tuesday&quot;
Case 4 WScript.Echo &quot;Wednesday&quot;
Case 5 WScript.Echo &quot;Thursday&quot;
Case 6 WScript.Echo &quot;Friday&quot;
Case 7 WScript.Echo &quot;Saturday&quot; End Select Else if dIsDefender = 1 then WScript.Echo &quot;No Scheduled Scan: Windows Defender&quot; else WScript.Echo &quot;Not found&quot; end if End If Else WScript.Echo &quot;Forefront Endpoint Protection not found&quot; End If
Function RegKeyExists(objRegistry, sHive, sRegKey) Dim aValueNames, aValueTypes If objRegistry.EnumValues(sHive, sRegKey, aValueNames, aValueTypes) = 0 Then RegKeyExists = True Else RegKeyExists = False End If End Function
Function Getx64RegistryProvider &#039; Returns the best available registry provider: 32 bit on 32 bit systems, 64 bit on 64 bit systems Dim objWMIService, colItems, objItem, iArchType, objCtx, objLocator, objServices, objRegProv Set objWMIService = GetObject(&quot;winmgmts:\\.\root\CIMV2&quot;) Set colItems = objWMIService.ExecQuery(&quot;Select SystemType from Win32_ComputerSystem&quot;) For Each objItem In colItems If InStr(LCase(objItem.SystemType), &quot;x64&quot;) &gt; 0 Then iArchType = 64 Else iArchType = 32 End If Next
Set objCtx = CreateObject(&quot;WbemScripting.SWbemNamedValueSet&quot;) objCtx.Add &quot;__ProviderArchitecture&quot;, iArchType Set objLocator = CreateObject(&quot;Wbemscripting.SWbemLocator&quot;) Set objServices = objLocator.ConnectServer(&quot;&quot;,&quot;root\default&quot;,&quot;&quot;,&quot;&quot;,,,,objCtx) Set objRegProv = objServices.Get(&quot;StdRegProv&quot;)
Set Getx64RegistryProvider = objRegProv End Function &#039; Getx64RegistryProvider

