#!/bin/bash

uptime | awk &#039;{
  # chops off &quot;up&quot; and everything before it:
  sub(/.*up[ ]+/,&quot;&quot;,$0)
  # chops off &quot;, # users&quot; and everything after it:       
  sub(/,[ ]+[0-9]+ user.*/,&quot;&quot;,$0)
  # cleans up extra spaces, i think:    
  sub(/,/,&quot;&quot;,$0)
  # obvious enough, prints the results                       
  print($0)
}&#039;

