&#039;======================================== &#039; Network Link Speed &#039;========================================
Option Explicit
Const cWMINameSpace = &quot;root/wmi&quot; Const cWMIInstance = &quot;MSNdis_LinkSpeed&quot; Const strComputerName = &quot;.&quot;
Dim strWinMgmts, objWMI, listAdapters, objAdapter,intLinkSpeed,strSep strSep = &quot;|&quot;
strWinMgMts = &quot;winmgmts:{impersonationLevel=impersonate}!//&quot; &amp; strComputerName &amp; &quot;/&quot; &amp; cWMINameSpace Set objWMI = GetObject(strWinMgMts)
If Err.Number &lt;&gt; 0 Then WScript.Echo &quot;ERROR: Unable to connect to the WMI namespace.&quot; Else Set listAdapters = objWMI.InstancesOf(cWMIInstance) If (listAdapters.count &gt; 0) Then For Each objAdapter in listAdapters If CDbl(objAdapter.NdisLinkSpeed) &lt; 0 Then intLinkSpeed = Abs(CDbl(objAdapter.NdislinkSpeed)) Else intLinkSpeed = CDbl(objAdapter.NdisLinkSpeed) End If WScript.Echo objAdapter.InstanceName &amp; strSep &amp; intLinkSpeed/10000 Next Else WScript.Echo &quot;WARNING: No adapters found.&quot; End If End IF

