#!/bin/bash
cpu=$(system_profiler SPHardwareDataType | grep &#039;Processor Name&#039; | awk -F&#039;:&#039;  &#039;{split($2,a,&quot; &quot;); print a[1]}&#039;)
if [ &quot;&quot; = &quot;$cpu&quot; ]
then
	echo &quot;Unknown&quot;
else
	echo &quot;$cpu&quot;
fi

