#!/bin/bash ############################################ ## Script Name: application_version.sh ## ## Created: 16 MAR 2014 ## ## Author: James Hoscheit - Tanium ## ## &lt;james.hoscheit@tanium.com&gt; ## ## Egon Rinderer - Tanium ## ## &lt;egon.rinderer@tanium.com&gt; ## ## Purpose: To query an application version on ## ## a Linux system. ## ############################################
file=&quot;||application||&quot;
#if [ &quot;$#&quot; -eq &quot;0&quot; ] #then # exit 1 #else # file=$1 #fi
if [ -f /bin/rpm ] #rpm based solutions then installedPkg=`rpm -q $file` if [ `echo $installedPkg | grep -v &#039;not installed&#039; | grep -c $file` -gt 0 ] then echo $installedPkg | awk -F&#039;el&#039; &#039;{print $1}&#039; | awk -F&#039;-&#039; &#039;{print $2}&#039; else echo &quot;not installed&quot; fi
elif [ -f /usr/bin/apt-get ] #.deb based solutions then installedPkg=`dpkg -l $file 2&gt;/dev/null` installedPkg2=`echo $installedPkg | sed &#039;s/^.*=== /ii.*$/&#039;` if [ `echo $installedPkg2 | egrep -c &#039;^ii&#039;` -gt 0 ] then echo $installedPkg2 | egrep &#039;^ii&#039; | awk &#039;{print $3}&#039; else echo &quot;not installed&quot; fi else echo &quot;unknown Linux installed&quot; fi

#!/bin/bash ############################################ ## Script Name: application_version.sh ## ## Created: 16 MAR 2014 ## ## Author: James Hoscheit - Tanium ## ## &lt;james.hoscheit@tanium.com&gt; ## ## Egon Rinderer - Tanium ## ## &lt;egon.rinderer@tanium.com&gt; ## ## Purpose: To query an application version on ## ## a Mac system. ## ############################################
version=`system_profiler SPApplicationsDataType | grep -i -A2 &quot;||application||&quot; | grep &quot;^ *Version&quot; | awk -F&#039;: &#039; &#039;{print $2}&#039;`
if [ ${#version} -eq 0 ] then echo &quot;Not installed&quot; else echo $version fi

