#!/bin/sh
tmpfile=&quot;/tmp/tanium.$$&quot;
lsof -p ^$PPID -iTCP -sTCP:LISTEN -P +c 15 -n &gt; $tmpfile
cat $tmpfile | awk &#039;$1 != &quot;COMMAND&quot; { print $1&quot;|&quot;$1&quot;|&quot;substr($9,index($9,&quot;:&quot;))}&#039;
rm -f $tmpfile

