#!/bin/bash
if [ -f /etc/hosts ]; then
file=&quot;/etc/hosts&quot; line=`cat $file`
while read -r line; do line=`echo $line | sed &#039;s/^ *//g&#039; | sed &#039;s/ *$//g&#039;`
if [[ &quot;$line&quot; =~ ^#.*$ ]]; then l=1 #do nothing else if [ -n &quot;$line&quot; ]; then
COLS=( $line ); # parses columns without executing a subshell #RESULT=&quot;${COLS[0]}&quot;; # returns first column (0-based indexes) echo &quot;${COLS[1]},${COLS[0]}&quot;
fi fi done &lt;&lt;&lt; &quot;$line&quot;
fi

