#!/bin/sh
LINUX=`uname -o | grep -c -i linux`

if [ $LINUX -eq 1 ]
then
lshal | grep -i info.product | grep CD | sed &#039;s/info.product = &#039;\&#039;&#039;\(.*\)&#039;\&#039;&#039;.*/\1/g&#039;
fi

