#!/bin/bash
video=$(system_profiler SPDisplaysDataType | grep Chipset | awk -F&#039;: &#039; &#039;{print $2}&#039;)
if [ &quot;&quot; = &quot;$video&quot; ]
then
	echo &quot;Unknown&quot;
else
	echo &quot;$video&quot;
fi

